import React, { useState } from "react";
import axios from "axios";
import { apiPost } from "../utils/api";

export default function AddMovieForm({ token, refresh, lists }) {
  const [query, setQuery] = useState("");
  const [results, setResults] = useState([]);
  const [title, setTitle] = useState("");
  const [year, setYear] = useState("");
  const [rating, setRating] = useState("");
  const [listName, setListName] = useState(lists?.[0] || "Default");

  // üîç Search movies via OMDb API
  async function searchNow() {
    if (!query.trim()) return alert("Enter a movie name");
    try {
      const res = await axios.get(
        `https://www.omdbapi.com/?apikey=e7b09c77&s=${encodeURIComponent(
          query
        )}`
      );

      if (res.data.Response === "True") {
        // map to your internal format
        const formatted = res.data.Search.map((m) => ({
          apiId: m.imdbID,
          title: m.Title,
          year: m.Year,
          poster:
            m.Poster && m.Poster !== "N/A"
              ? m.Poster
              : "https://via.placeholder.com/100x150?text=No+Image",
          type: m.Type,
        }));
        setResults(formatted);
      } else {
        setResults([]);
        alert("No results found");
      }
    } catch (err) {
      console.error("Search failed:", err);
      alert("Search failed");
    }
  }

  // ‚ûï Add movie to backend (from search result)
  async function addFromResult(item, list) {
    try {
      await apiPost("/api/movies", token, {
        title: item.title,
        apiId: item.apiId,
        poster: item.poster,
        type: item.type,
        listName: list || "Default",
        status: "Plan to Watch",
      });
      alert(`‚úÖ Added "${item.title}"`);
      refresh();
    } catch (err) {
      console.error("Add failed:", err.response?.data || err);
      alert("‚ùå Add failed");
    }
  }

  // üßæ Manual Add (no search)
  async function addManual() {
    if (!title.trim()) return alert("Enter title");
    try {
      await apiPost("/api/movies", token, {
        title,
        year: Number(year || 0),
        rating: Number(rating || 0),
        listName,
      });
      alert("‚úÖ Movie added manually!");
      setTitle("");
      setYear("");
      setRating("");
      refresh();
    } catch (err) {
      console.error("Manual add failed:", err);
      alert("‚ùå Add failed");
    }
  }

  return (
    <div className="card">
      <h3>Add / Search</h3>

      {/* Search Bar */}
      <div style={{ display: "flex", gap: 8, marginBottom: 10 }}>
        <input
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          placeholder="Search OMDb..."
          style={{ flex: 1 }}
        />
        <button className="btn" onClick={searchNow}>
          Search
        </button>
      </div>

      {/* Search Results */}
      <div style={{ marginTop: 8 }}>
        {results.map((r) => (
          <div
            key={r.apiId}
            style={{
              display: "flex",
              gap: 8,
              alignItems: "center",
              marginTop: 8,
              padding: "6px 0",
              borderBottom: "1px solid #444",
            }}
          >
            <img
              src={r.poster}
              style={{ width: 60, height: 90, objectFit: "cover" }}
              alt="poster"
            />
            <div style={{ flex: 1 }}>
              <strong>{r.title}</strong> <div className="muted">{r.year}</div>
              <div style={{ marginTop: 6 }}>
                <select
                  defaultValue={listName}
                  onChange={(e) => setListName(e.target.value)}
                >
                  {lists.map((l) => (
                    <option key={l}>{l}</option>
                  ))}
                </select>
                <button
                  className="btn"
                  onClick={() => addFromResult(r, listName)}
                  style={{ marginLeft: 8 }}
                >
                  + Add
                </button>
              </div>
            </div>
          </div>
        ))}
      </div>

      <hr style={{ margin: "15px 0" }} />

      {/* Manual Add Section */}
      <h4>Manual Add</h4>
      <input
        value={title}
        onChange={(e) => setTitle(e.target.value)}
        placeholder="Title"
      />
      <input
        value={year}
        onChange={(e) => setYear(e.target.value)}
        placeholder="Year"
        type="number"
      />
      <input
        value={rating}
        onChange={(e) => setRating(e.target.value)}
        placeholder="Rating (1-10)"
        type="number"
      />
      <select
        value={listName}
        onChange={(e) => setListName(e.target.value)}
        style={{ display: "block", marginTop: 8 }}
      >
        {lists.map((l) => (
          <option key={l}>{l}</option>
        ))}
      </select>
      <button className="btn" onClick={addManual} style={{ marginTop: 8 }}>
        Add Manual
      </button>
    </div>
  );
}
