import React, { useState } from 'react';
export default function Search({ token, onAdded }) {
  const [q, setQ] = useState('');
  const [type, setType] = useState('movie');
  const [results, setResults] = useState([]);
  const search = async e => {
    e?.preventDefault();
    if (!q) return;
    const res = await fetch(`http://localhost:5000/api/movies/search?type=${type}&q=${encodeURIComponent(q)}`, {
      headers: { Authorization: 'Bearer ' + token }
    });
    const data = await res.json();
    setResults(data.results || []);
  };
  const add = async (item) => {
    const detRes = await fetch(`http://localhost:5000/api/movies/details?type=${type}&id=${item.id}`, {
      headers: { Authorization: 'Bearer ' + token }
    });
    const details = await detRes.json();
    const body = {
      title: details.title,
      poster: details.image,
      apiId: details.id,
      type: type === 'anime' ? 'Anime' : 'Movie',
      status: 'Plan to Watch'
    };
    const addRes = await fetch('http://localhost:5000/api/movies', {
      method: 'POST', headers: { 'Content-Type':'application/json', Authorization: 'Bearer ' + token },
      body: JSON.stringify(body)
    });
    const added = await addRes.json();
    if (addRes.ok) { onAdded(added); alert('Added to watchlist'); } else alert(added.message || 'Failed to add');
  };
  return (
    <div className='card'>
      <h3>Search (Movies & Anime)</h3>
      <form onSubmit={search}>
        <div style={{display:'flex', gap:8}}>
          <input placeholder='Search title...' value={q} onChange={e=>setQ(e.target.value)} />
          <select value={type} onChange={e=>setType(e.target.value)}>
            <option value='movie'>Movie / Series</option>
            <option value='anime'>Anime</option>
          </select>
          <button className='btn small' type='submit'>Search</button>
        </div>
      </form>
      <div className='search-results'>
        {results.map(r=> (
          <div key={r.id} className='result' style={{display:'flex', gap:12, alignItems:'center'}}>
            <img src={r.image || 'https://via.placeholder.com/80x120?text=No+Image'} alt={r.title} style={{width:60,height:90,objectFit:'cover',borderRadius:4}}/>
            <div>
              <strong>{r.title}</strong> <div className='muted'>{r.year}</div>
              <div className='row'><button className='btn tiny' onClick={()=>add(r)}>+ Add to Watchlist</button></div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
