// frontend/src/components/AddMovieForm.jsx
import React, { useState } from "react";
import { apiGet, apiPost } from "../utils/api";

const token = localStorage.getItem("ct_token");
await apiPost("/api/movies", token, { title, year, rating, listName });

export default function AddMovieForm({ token, refresh, lists }) {
  const [q, setQ] = useState("");
  const [results, setResults] = useState([]);
  const [title, setTitle] = useState("");
  const [year, setYear] = useState("");
  const [rating, setRating] = useState("");
  const [listName, setListName] = useState(lists?.[0] || "Default");
  


  // --- SEARCH FUNCTION ---
  async function searchNow() {
    if (!q.trim()) return;
    try {
      const data = await apiGet(`/api/movies/search?q=${encodeURIComponent(q)}`, token);
      setResults(data);
    } catch (err) {
      console.error("Search failed:", err);
      alert("Search failed — check backend connection or OMDb key");
    }
  }

  // --- ADD FROM SEARCH RESULT ---
  async function addFromResult(item, list) {
    try {
      await apiPost("/api/movies", token, {
        title: item.title,
        year: item.year || 0,
        apiId: item.apiId,
        poster: item.poster,
        type: item.type,
        listName: list || "Default",
      });
      refresh();
      alert("Added successfully!");
    } catch (err) {
      console.error("Add from search failed:", err);
      alert("Add failed — check backend logs for details.");
    }
  }

  // --- MANUAL ADD ---
  async function addManual() {
    if (!title.trim()) return alert("Enter title");
    try {
      await apiPost("/api/movies", token, {
        title,
        year: Number(year || 0),
        rating: Number(rating || 0),
        listName,
      });
      setTitle("");
      setYear("");
      setRating("");
      refresh();
      alert("Added manually!");
    } catch (err) {
      console.error("Manual add failed:", err);
      alert("Add failed — check backend logs for details.");
    }
  }

  return (
    <div className="card">
      <h3>Add / Search Movies</h3>

      {/* --- SEARCH BAR --- */}
      <div style={{ display: "flex", gap: 8 }}>
        <input
          value={q}
          onChange={(e) => setQ(e.target.value)}
          placeholder="Search OMDb..."
        />
        <button className="btn" onClick={searchNow}>
          Search
        </button>
      </div>

      {/* --- SEARCH RESULTS --- */}
      <div style={{ marginTop: 8 }}>
        {results.map((r) => (
          <div
            key={r.apiId}
            style={{
              display: "flex",
              gap: 8,
              alignItems: "center",
              marginTop: 8,
            }}
          >
            <img
              src={r.poster || "https://via.placeholder.com/80x120"}
              style={{ width: 60, height: 90, objectFit: "cover" }}
              alt="poster"
            />
            <div style={{ flex: 1 }}>
              <strong>{r.title}</strong>
              <div className="muted">{r.year}</div>
              <div style={{ marginTop: 6 }}>
                <select
                  defaultValue={listName}
                  onChange={(e) => setListName(e.target.value)}
                >
                  {lists.map((l) => (
                    <option key={l}>{l}</option>
                  ))}
                </select>
                <button
                  className="btn"
                  onClick={() => addFromResult(r, listName)}
                  style={{ marginLeft: 8 }}
                >
                  + Add
                </button>
              </div>
            </div>
          </div>
        ))}
      </div>

      <hr />

      {/* --- MANUAL ADD SECTION --- */}
      <h4>Manual Add</h4>
      <input
        value={title}
        onChange={(e) => setTitle(e.target.value)}
        placeholder="Title"
      />
      <input
        value={year}
        onChange={(e) => setYear(e.target.value)}
        placeholder="Year"
        type="number"
      />
      <input
        value={rating}
        onChange={(e) => setRating(e.target.value)}
        placeholder="Rating (1-10)"
        type="number"
      />
      <select
        value={listName}
        onChange={(e) => setListName(e.target.value)}
      >
        {lists.map((l) => (
          <option key={l}>{l}</option>
        ))}
      </select>
      <button className="btn" onClick={addManual}>
        Add Manual
      </button>
    </div>
  );
}
