import React, { useState } from "react";

export default function Search({ onAdded }) {
  const [q, setQ] = useState("");
  const [type, setType] = useState("movie");
  const [results, setResults] = useState([]);

  const token = localStorage.getItem("ct_token");

  async function searchNow() {
    if (!q.trim()) return;

    const url = `${process.env.REACT_APP_API_URL}/api/movies/search?q=${q}&type=${type}`;
    const res = await fetch(url, { headers: { Authorization: "Bearer " + token } });
    const data = await res.json();
    setResults(Array.isArray(data) ? data : []);
  }

  async function add(item) {
    const listName = prompt("Add to which list?", "Default");

    const res = await fetch(`${process.env.REACT_APP_API_URL}/api/movies`, {
      method: "POST",
      headers: { "Content-Type": "application/json", Authorization: "Bearer " + token },
      body: JSON.stringify({ ...item, listName }),
    });

    if (res.ok && onAdded) onAdded();
  }

  return (
    <div>
      <input value={q} onChange={(e) => setQ(e.target.value)} placeholder="Search movie..." />
      <select value={type} onChange={(e) => setType(e.target.value)}>
        <option value="movie">Movie</option>
        <option value="series">Series</option>
      </select>
      <button onClick={searchNow}>Search</button>

      {results.map((r) => (
        <div key={r.apiId}>
          <img src={r.poster} alt="" width={80} />
          <div>{r.title}</div>
          <button onClick={() => add(r)}>+ Add</button>
        </div>
      ))}
    </div>
  );
}
