import React from 'react';
import { apiPut, apiDelete } from '../utils/api';

function Group({ title, items, token, refresh }) {
  return (
    <div className='group'>
      <h4>{title} ({items.length})</h4>
      <div className='cards'>
        {items.map(it => (
          <div key={it._id} className='card small-card'>
            <img src={it.poster || 'https://via.placeholder.com/120x170?text=No+Image'} alt={it.title}/>
            <div>
              <strong>{it.title}</strong>
              <div className='muted'>{it.type}</div>
              <div>Rating: {it.rating || '-'}</div>
              <div className='row'>
                <select defaultValue={it.status || "Plan to Watch"} 
                  onChange={async e=>{ 
                    await apiPut(`/api/movies/${it._id}`, token, { status: e.target.value });
                    refresh(); 
                  }}>
                  <option>Plan to Watch</option>
                  <option>Watching</option>
                  <option>Completed</option>
                  <option>Dropped</option>
                </select>

                <button className='btn tiny' onClick={async ()=>{
                  const r = prompt('Set rating 0-10', it.rating || 0);
                  const rv = Number(r);
                  if (!isNaN(rv)) { 
                    await apiPut(`/api/movies/${it._id}`, token, { rating: rv });
                    refresh(); 
                  }
                }}>Rate</button>

                <button className='btn danger tiny' onClick={async ()=>{
                  if (confirm('Delete this item?')) { 
                    await apiDelete(`/api/movies/${it._id}`, token); 
                    refresh(); 
                  }
                }}>Delete</button>
              </div>

              <div><em>{it.review}</em>
                <div>
                  <button className='btn tiny' onClick={async ()=>{
                    const review = prompt('Edit review', it.review || '');
                    if (review !== null) { 
                      await apiPut(`/api/movies/${it._id}`, token, { review });
                      refresh(); 
                    }
                  }}>Edit review</button>
                </div>
              </div>

            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default function Dashboard({ movies, token, refresh }) {
  const groups = { 'Plan to Watch': [], 'Watching': [], 'Completed': [], 'Dropped': [] };

  movies.forEach(m => {
    const status = m.status || "Plan to Watch"; // default
    groups[status].push(m);
  });

  return (
    <div className='dashboard'>
      {Object.keys(groups).map(k=> 
        <Group key={k} title={k} items={groups[k]} token={token} refresh={refresh} />
      )}
    </div>
  );
}
