import React, { useEffect, useState } from 'react';
import AddMovieForm from './AddMovieForm';
import Group from './Group';
import { apiGet, apiPost } from '../utils/api';

export default function Dashboard({ token }) {
  const [movies, setMovies] = useState([]);
  const [lists, setLists] = useState([]);
  const [newListName, setNewListName] = useState('');

  async function refresh() {
    try {
      const r = await apiGet('/api/movies', token);
      setMovies(r.data || r);
      const listsR = await apiGet('/api/lists', token);
      const myLists = (listsR.data || listsR).map(l=> l.name);
      setLists(myLists.length ? myLists : ['Default']);
    } catch (err) { console.error(err); }
  }

  useEffect(()=> { refresh(); }, []);

  async function createList() {
    if (!newListName.trim()) return;
    try {
      await apiPost('/api/lists', token, { name: newListName, isPublic: false });
      setNewListName('');
      refresh();
    } catch (err) { console.error(err); alert('Create list failed'); }
  }

  // group movies by listName, then within each list group by status
  const listsMap = {};
  movies.forEach(m => {
    const ln = m.listName || 'Default';
    if (!listsMap[ln]) listsMap[ln] = [];
    listsMap[ln].push(m);
  });

  return (
    <div>
      <div style={{display:'flex',gap:12,alignItems:'center',marginBottom:12}}>
        <input value={newListName} onChange={e=>setNewListName(e.target.value)} placeholder="New list name"/>
        <button className="btn" onClick={createList}>Create List</button>
      </div>

      <AddMovieForm token={token} refresh={refresh} lists={lists.length ? lists : ['Default']} />

      {Object.keys(listsMap).map(listName => (
        <div key={listName} className="list-block card">
          <h2>üìÅ {listName}</h2>
          {['Plan to Watch','Watching','Completed','Dropped'].map(status => (
            <Group key={status} title={status} items={listsMap[listName].filter(m=>m.status===status)} token={token} refresh={refresh} />
          ))}
        </div>
      ))}

    </div>
  );
}
