import React, { useState } from 'react';

export default function Search({ onAdded }) {
  const [q, setQ] = useState('');
  const [type, setType] = useState('movie');
  const [results, setResults] = useState([]);

  const token = localStorage.getItem("ct_token");

  async function searchNow() {
    if (!q.trim()) return;

    const url = `${process.env.REACT_APP_API_URL}/api/movies/search?q=${q}&type=${type}`;
    const res = await fetch(url, {
      headers: { Authorization: "Bearer " + token }
    });

    const data = await res.json();
    console.log("Search API returned:", data);

    if (Array.isArray(data.results)) {
      setResults(data.results);
    } else if (Array.isArray(data)) {
      setResults(data);
    } else {
      setResults([]);
    }
  }

async function add(item) {
  console.log("Trying to add item:", item);
  console.log("Token present:", token);

  const res = await fetch(`${process.env.REACT_APP_API_URL}/api/movies`, {
    method: "POST",
    headers: {
      "Content-Type": "application/json",
      Authorization: "Bearer " + token,
    },
    body: JSON.stringify({
      apiId: item.apiId,
      title: item.title,
      poster: item.poster   // we will adjust naming after checking backend schema
    }),
  });

  const data = await res.json();
  console.log("Server Response:", data);

  if (res.ok && onAdded) onAdded();
}

  return (
    <div>
      <input value={q} onChange={e => setQ(e.target.value)} placeholder="Search movie..." />
      <button onClick={searchNow}>Search</button>

      {results.map((r) => (
        <div key={r.apiId}>
          <img src={r.poster} alt="" width={80} />
          <div>{r.title}</div>
          <button onClick={() => add(r)}>+ Add</button>
        </div>
      ))}
    </div>
  );
}
