export async function apiFetch(path, token, options = {}) {
  const res = await fetch(`${process.env.REACT_APP_API_URL}${path}`, {
    ...options,
    headers: {
      "Content-Type": "application/json",
      Authorization: "Bearer " + token,
      ...(options.headers || {})
    }
  });

  if (!res.ok) throw new Error("API Request Failed");
  return res.json();
}

export const apiPost = (path, token, data) =>
  apiFetch(path, token, { method: "POST", body: JSON.stringify(data) });

export const apiPut = (path, token, data) =>
  apiFetch(path, token, { method: "PUT", body: JSON.stringify(data) });

export const apiDelete = (path, token) =>
  apiFetch(path, token, { method: "DELETE" });
