import { useState } from "react";
import Group from "./Group";
import AddMovieForm from "./AddMovieForm";
import AddListForm from "./AddListForm";

const STATUS_GROUPS = ["Plan to Watch", "Watching", "Completed", "Dropped"];

export default function Dashboard({ movies, token, refresh }) {
  const [lists, setLists] = useState(["Default", "Favorites", "Romance"]); // Starting lists

  return (
    <div className="dashboard">

      <h2>Your Collections</h2>

      <AddListForm lists={lists} setLists={setLists} />
      <AddMovieForm token={token} refresh={refresh} lists={lists} />

      {lists.map(listName => {
        const filtered = movies.filter(m => m.list === listName);

        return (
          <div key={listName} className="list-block">
            <h3>üìÅ {listName}</h3>

            {STATUS_GROUPS.map(status => (
              <Group
                key={status}
                title={status}
                items={filtered.filter(m => m.status === status)}
                token={token}
                refresh={refresh}
              />
            ))}
          </div>
        );
      })}
    </div>
  );
}
