// frontend/src/utils/api.js
import axios from 'axios';

const BASE = process.env.REACT_APP_API_URL || 'http://localhost:5000';

export const apiGet = (path, token) => axios.get(BASE + path, { headers: token ? { Authorization: 'Bearer ' + token } : {} });
export const apiPost = (path, token, body) => axios.post(BASE + path, body, { headers: token ? { Authorization: 'Bearer ' + token } : {} });
export const apiPut = (path, token, body) => axios.put(BASE + path, body, { headers: token ? { Authorization: 'Bearer ' + token } : {} });
export const apiDelete = (path, token) => axios.delete(BASE + path, { headers: token ? { Authorization: 'Bearer ' + token } : {} });
