export const API_BASE = process.env.REACT_APP_API_BASE || 'http://localhost:5000';

function buildHeaders(token) {
  const headers = { 'Content-Type': 'application/json' };
  if (token) headers['Authorization'] = 'Bearer ' + token;
  return headers;
}

export function apiFetch(path, token, opts = {}) {
  const headers = { ...(opts.headers || {}), ...buildHeaders(token) };
  return fetch(API_BASE + path, { ...opts, headers });
}

export function apiGet(path, token) { return apiFetch(path, token, { method: 'GET' }); }
export function apiPost(path, token, body) { return apiFetch(path, token, { method: 'POST', body: JSON.stringify(body) }); }
export function apiPut(path, token, body) { return apiFetch(path, token, { method: 'PUT', body: JSON.stringify(body) }); }
export function apiDelete(path, token) { return apiFetch(path, token, { method: 'DELETE' }); }
