import { apiPut, apiDelete } from "../utils/api";

export default function Group({ title, items, token, refresh }) {
  return (
    <div className="group">
      <h4>{title} ({items.length})</h4>

      <div className="cards">
        {items.map(it => (
          <div key={it._id} className="card">
            <img src={it.poster || "https://via.placeholder.com/120x170?text=No+Image"} alt={it.title} />

            <div className="card-body">
              <strong>{it.title}</strong>
              <div className="muted">{it.type}</div>

              <div>Rating: {it.rating ?? "-"}</div>

              <select
                defaultValue={it.status}
                onChange={async e => {
                  await apiPut(`/api/movies/${it._id}`, token, { status: e.target.value });
                  refresh();
                }}
              >
                <option>Plan to Watch</option>
                <option>Watching</option>
                <option>Completed</option>
                <option>Dropped</option>
              </select>

              <button
                className="btn tiny"
                onClick={async () => {
                  const r = prompt("Rate 0-10", it.rating || 0);
                  const rv = Number(r);
                  if (!isNaN(rv)) {
                    await apiPut(`/api/movies/${it._id}`, token, { rating: rv });
                    refresh();
                  }
                }}
              >
                Rate
              </button>

              <button
                className="btn danger tiny"
                onClick={async () => {
                  if (confirm("Delete this item?")) {
                    await apiDelete(`/api/movies/${it._id}`, token);
                    refresh();
                  }
                }}
              >
                Delete
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
