import React, { useEffect, useState } from "react";
import axios from "axios";

const API = "http://localhost:5000/movies";

function App() {
  const [movies, setMovies] = useState([]);
  const [form, setForm] = useState({ title: "", year: "", rating: "" });

  const getMovies = async () => {
    const res = await axios.get(API);
    setMovies(res.data);
  };

  const addMovie = async () => {
    await axios.post(`${API}/add`, form, {
      headers: { authorization: "mysecrettoken" },
    });
    setForm({ title: "", year: "", rating: "" });
    getMovies();
  };

  const toggleWatch = async (id) => {
    await axios.put(`${API}/${id}/watch`, {}, {
      headers: { authorization: "mysecrettoken" },
    });
    getMovies();
  };

  const deleteMovie = async (id) => {
    await axios.delete(`${API}/${id}`, {
      headers: { authorization: "mysecrettoken" },
    });
    getMovies();
  };

  useEffect(() => {
    getMovies();
  }, []);

  return (
    <div style={{ padding: 20 }}>
      <h1>üé¨ CineTrack</h1>

      <input
        placeholder="Title"
        value={form.title}
        onChange={(e) => setForm({ ...form, title: e.target.value })}
      />
      <input
        placeholder="Year"
        value={form.year}
        onChange={(e) => setForm({ ...form, year: e.target.value })}
      />
      <input
        placeholder="Rating"
        value={form.rating}
        onChange={(e) => setForm({ ...form, rating: e.target.value })}
      />
      <button onClick={addMovie}>Add Movie</button>

      <ul>
        {movies.map((m) => (
          <li key={m._id}>
            {m.title} ({m.year}) ‚≠ê {m.rating} ‚Äî 
            {m.watched ? "‚úÖ Watched" : "üïí Not Watched"}
            <button onClick={() => toggleWatch(m._id)}>
              Toggle Watch
            </button>
            <button onClick={() => deleteMovie(m._id)}>
              ‚ùå Delete
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
