import React, { useEffect, useState } from "react";
import { apiGet, apiPut, apiDelete } from "../utils/api";
import AddMovieForm from "../components/AddMovieForm";

export default function Dashboard() {
  const [movies, setMovies] = useState([]);
  const [filter, setFilter] = useState("All");
  const token = localStorage.getItem("ct_token");

  async function loadMovies() {
    try {
      const data = await apiGet("/api/movies", token);
      setMovies(Array.isArray(data) ? data : []);
    } catch (err) {
      console.error("Load Movies Error:", err);
      alert("Failed to load movies");
    }
  }

  useEffect(() => {
    loadMovies();
  }, []);

  const filteredMovies =
    filter === "All"
      ? movies
      : movies.filter((m) => m.status === filter);

  async function updateMovie(id, changes) {
    try {
      await apiPut(`/api/movies/${id}`, token, changes);
      loadMovies();
    } catch (err) {
      console.error("Update failed:", err);
      alert("Failed to update movie");
    }
  }

  async function deleteMovie(id) {
    if (!window.confirm("Delete this movie?")) return;
    try {
      await apiDelete(`/api/movies/${id}`, token);
      loadMovies();
    } catch (err) {
      console.error("Delete failed:", err);
      alert("Failed to delete");
    }
  }

  return (
    <div className="app">
      <header className="header">
        <h2>ðŸŽ¬ My Watchlist</h2>
        <select
          value={filter}
          onChange={(e) => setFilter(e.target.value)}
          className="filter-select"
        >
          <option>All</option>
          <option>Plan to Watch</option>
          <option>Watching</option>
          <option>Completed</option>
          <option>Dropped</option>
        </select>
      </header>

      <AddMovieForm token={token} refresh={loadMovies} lists={["Default", "Favorites", "Custom"]} />

      <div className="cards">
        {filteredMovies.length === 0 ? (
          <div className="muted">No movies yet.</div>
        ) : (
          filteredMovies.map((movie) => (
            <div key={movie._id} className="small-card">
              <img
                src={
                  movie.poster ||
                  "https://via.placeholder.com/80x120?text=No+Image"
                }
                alt={movie.title}
              />
              <div className="card-info">
                <strong>{movie.title}</strong>
                <div className="muted">{movie.year || "â€”"}</div>
                <div style={{ marginTop: "4px" }}>
                  <label>Status:</label>
                  <select
                    value={movie.status}
                    onChange={(e) =>
                      updateMovie(movie._id, { status: e.target.value })
                    }
                  >
                    <option>Plan to Watch</option>
                    <option>Watching</option>
                    <option>Completed</option>
                    <option>Dropped</option>
                  </select>
                </div>

                <div style={{ marginTop: "4px" }}>
                  <label>Rating:</label>
                  <input
                    type="number"
                    min="0"
                    max="10"
                    value={movie.rating || ""}
                    onChange={(e) =>
                      updateMovie(movie._id, {
                        rating: Number(e.target.value),
                      })
                    }
                    style={{
                      width: "60px",
                      marginLeft: "6px",
                      padding: "2px 4px",
                    }}
                  />
                </div>

                <div style={{ marginTop: "8px" }}>
                  <button
                    className="btn danger tiny"
                    onClick={() => deleteMovie(movie._id)}
                  >
                    Delete
                  </button>
                </div>
              </div>
            </div>
          ))
        )}
      </div>

      <footer className="footer">
        <p>Made for your cinematic journey.</p>
      </footer>
    </div>
  );
}
