import React from "react";
import { apiPut, apiDelete } from "../utils/api";

function Group({ title, items, token, refresh }) {
  return (
    <div className="group">
      <h3>{title} ({items.length})</h3>

      <div className="cards">
        {items.map((it) => (
          <div key={it._id} className="card small-card">
            <img src={it.poster || "https://via.placeholder.com/120x170"} alt="" />
            <strong>{it.title}</strong>

            <select defaultValue={it.listName} onChange={async (e) => { await apiPut(`/api/movies/${it._id}`, token, { listName: e.target.value }); refresh(); }}>
              <option>Default</option>
              <option>Favorites</option>
              <option>Anime</option>
              <option>Thrillers</option>
            </select>

            <select defaultValue={it.status} onChange={async (e) => { await apiPut(`/api/movies/${it._id}`, token, { status: e.target.value }); refresh(); }}>
              <option>Plan to Watch</option>
              <option>Watching</option>
              <option>Completed</option>
              <option>Dropped</option>
            </select>

            <button onClick={async () => { if (window.confirm("Delete?")) { await apiDelete(`/api/movies/${it._id}`, token); refresh(); } }}>Delete</button>
          </div>
        ))}
      </div>
    </div>
  );
}

export default function Dashboard({ movies, token, refresh }) {
  const groups = {};

  movies.forEach((m) => {
    const list = m.listName || "Default";
    if (!groups[list]) groups[list] = [];
    groups[list].push(m);
  });

  return (
    <div>
      {Object.keys(groups).map((list) => (
        <Group key={list} title={list} items={groups[list]} token={token} refresh={refresh} />
      ))}
    </div>
  );
}
