import { useState } from "react";
import { apiPost } from "../utils/api";

export default function AddMovieForm({ token, refresh, lists }) {
  const [title, setTitle] = useState("");
  const [list, setList] = useState("Default");

  const addMovie = async () => {
    if (!title.trim()) return alert("Enter a title");
    await apiPost("/api/movies", token, { title, list });
    setTitle("");
    refresh();
  };

  return (
    <div className="add-form">
      <input
        type="text"
        placeholder="Enter movie/anime title"
        value={title}
        onChange={e => setTitle(e.target.value)}
      />

      <select value={list} onChange={e => setList(e.target.value)}>
        {lists.map(l => <option key={l}>{l}</option>)}
      </select>

      <button className="btn" onClick={addMovie}>Add</button>
    </div>
  );
}
